mapper.select:
SELECT price
FROM daily_price
WHERE product_code = #{productCode}
  AND price_date < #{priceDate}
ORDER BY price_date DESC
    LIMIT 1

SELECT EXISTS (
    SELECT 1
    FROM daily_price
    WHERE product_code = #{product.productCode}
      AND price_date = #{priceDate}
)

